mapper.select:
SELECT

FROM
tb_bridge_info


        AND id = #{id}


        AND structure_id = #{structureId}


        AND cost = #{cost}


        AND road_grade = #{roadGrade}


        AND posting_standard = #{postingStandard}


        AND quake_intensity = #{quakeIntensity}


        AND skew_angle = #{skewAngle}


        AND span_num = #{spanNum}


        AND span_combination = #{spanCombination}


        AND area = #{area}


        AND total_length = #{totalLength}


        AND total_width = #{totalWidth}


        AND roadway_width = #{roadwayWidth}


        AND sidewalk_width = #{sidewalkWidth}


        AND streamway_grade = #{streamwayGrade}


        AND highest_stage = #{highestStage}


        AND usual_stage = #{usualStage}


        AND up_main_beam_form = #{upMainBeamForm}


        AND up_main_beam_size = #{upMainBeamSize}


        AND up_main_beam_quantity = #{upMainBeamQuantity}


        AND up_cross_beam_form = #{upCrossBeamForm}


        AND clearance_span = #{clearanceSpan}


        AND lower_limit = #{lowerLimit}


        AND up_rise_span = #{upRiseSpan}


        AND up_bearing_form = #{upBearingForm}


        AND up_bearing_num = #{upBearingNum}


        AND up_deck_composition = #{upDeckComposition}


        AND up_pavement_land = #{upPavementLand}


        AND up_expansion_form = #{upExpansionForm}


        AND up_expansion_quantity = #{upExpansionQuantity}


        AND up_main_longitudinal_slope = #{upMainLongitudinalSlope}


        AND up_main_cross_slope = #{upMainCrossSlope}


        AND up_approach_longitudinal_slope = #{upApproachLongitudinalSlope}


        AND up_approach_cross_slope = #{upApproachCrossSlope}


        AND pier_form = #{pierForm}


        AND pier_num = #{pierNum}


        AND pier_elevation = #{pierElevation}


        AND pier_cap_size = #{pierCapSize}


        AND pier_base_elevation = #{pierBaseElevation}


        AND pier_floor_size = #{pierFloorSize}


        AND pier_pile_size = #{pierPileSize}


        AND pier_pile_num = #{pierPileNum}


        AND abutment_form = #{abutmentForm}


        AND abutment_num = #{abutmentNum}


        AND abutment_elevation = #{abutmentElevation}


        AND abutment_base_elevation = #{abutmentBaseElevation}


        AND abutment_cap_size = #{abutmentCapSize}


        AND abutment_baseboard_size = #{abutmentBaseboardSize}


        AND abutment_pile_size = #{abutmentPileSize}


        AND abutment_pile_num = #{abutmentPileNum}


        AND abutment_retain_thick = #{abutmentRetainThick}


        AND abutment_wing_wall_form = #{abutmentWingWallForm}


        AND abutment_wing_wall_length = #{abutmentWingWallLength}


        AND auxiliary_gulley_size = #{auxiliaryGulleySize}


        AND auxiliary_gulley_num = #{auxiliaryGulleyNum}


        AND water_drain_pipe_size = #{waterDrainPipeSize}


        AND water_drain_pipe_length = #{waterDrainPipeLength}


        AND auxiliary_rail_length = #{auxiliaryRailLength}


        AND auxiliary_rail_struction = #{auxiliaryRailStruction}


        AND auxiliary_boundary_size = #{auxiliaryBoundarySize}


        AND auxiliary_revetment_type = #{auxiliaryRevetmentType}


        AND auxiliary_barricade_type = #{auxiliaryBarricadeType}


        AND pipeline_water = #{pipelineWater}


        AND pipeline_gas = #{pipelineGas}


        AND pipeline_electricity = #{pipelineElectricity}


        AND pipeline_cable = #{pipelineCable}


        AND status = #{status}


        AND create_time = #{createTime}


        AND modify_time = #{modifyTime}


ORDER BY
id DESC

